head.script:
google.load("visualization", "1", {packages:["corechart"]});	
google.setOnLoadCallback(drawChart);
function drawChart() {
var options = {
    backgroundColor: '${backgroundColor}' ,
    legend: {position: '${position}' , alignment:"center"} ,
    title: '${title}',
    chartArea: {width: '95%'},
    titlePosition: 'in',
    axisTitlesPosition: 'in',
    hAxis: {textPosition: 'in'},
    vAxis: {textPosition: 'in'}
  };
  var data=${dataTable} ; 
  var chart  = new google.visualization.${ChartFunction}(document.getElementById('chart_div'));
  chart.draw(data, options);

}
